-- Drop the table if it already exists
DROP TABLE IF EXISTS dim.dim_zone_mapping;

-- Create the dim.dim_zone_mapping lookup table
CREATE TABLE dim.dim_zone_mapping (
    location_id INT PRIMARY KEY,
    borough VARCHAR(50),
    zone VARCHAR(100),
    service_zone VARCHAR(50),
    latitude FLOAT,
    longitude FLOAT,
    geolocation VARCHAR(100)
);

